element.code_text:
var restCaller = new com.kahuna.logic.lib.rest.RestCaller(this); 
 var url = "http://localhost:8080/KahunaService/rest/abl/demo/demo1/purchaseorder";  
 var key = containingRow.name;  
 log.debug("..using key: " + key);  
 url = url + "?filter=customer_name=%22" + key + "%22";   
 url = url.replace(/ /g, "%20");  
 var response = restCaller.get(url, null, {headers:{Authorization: "Kahuna demo_full:123"}} ); 
 return response;

